CREATE TABLE juego_wiitdb (
	"idJuegoWIITDB" INTEGER NOT NULL, 
	idgame VARCHAR(6), 
	name VARCHAR(255), 
	region VARCHAR(40), 
	developer VARCHAR(100), 
	publisher VARCHAR(100), 
	fecha_lanzamiento DATE, 
	wifi_players INTEGER, 
	input_players INTEGER, 
	"idRatingType" INTEGER, 
	"idRatingValue" INTEGER, 
	PRIMARY KEY ("idJuegoWIITDB"), 
	 FOREIGN KEY("idRatingType") REFERENCES rating_type ("idRatingType"), 
	 FOREIGN KEY("idRatingValue") REFERENCES rating_value ("idRatingValue")
);
CREATE TABLE online_features (
	"idFeature" INTEGER NOT NULL, 
	valor VARCHAR(255), 
	PRIMARY KEY ("idFeature")
);
CREATE TABLE particion (
	"idParticion" INTEGER NOT NULL, 
	device VARCHAR(10), 
	tipo VARCHAR(5), 
	usado FLOAT, 
	libre FLOAT, 
	total FLOAT, 
	fabricante VARCHAR(255), 
	PRIMARY KEY ("idParticion")
);
CREATE TABLE preferencias (
	id INTEGER NOT NULL, 
	ruta_anadir VARCHAR(255), 
	ruta_anadir_directorio VARCHAR(255), 
	ruta_extraer_iso VARCHAR(255), 
	ruta_copiar_caratulas VARCHAR(255), 
	ruta_copiar_discos VARCHAR(255), 
	PRIMARY KEY (id)
);
CREATE TABLE rating_content (
	"idRatingContent" INTEGER NOT NULL, 
	"idRatingType" INTEGER, 
	valor VARCHAR(255), 
	PRIMARY KEY ("idRatingContent"), 
	 FOREIGN KEY("idRatingType") REFERENCES rating_type ("idRatingType")
);
CREATE TABLE rating_type (
	"idRatingType" INTEGER NOT NULL, 
	tipo VARCHAR(255), 
	PRIMARY KEY ("idRatingType")
);
CREATE TABLE rating_value (
	"idRatingValue" INTEGER NOT NULL, 
	"idRatingType" INTEGER, 
	valor VARCHAR(255), 
	PRIMARY KEY ("idRatingValue"), 
	 FOREIGN KEY("idRatingType") REFERENCES rating_type ("idRatingType")
);
CREATE TABLE rel_accesorio_juego_obligatorio (
	"idJuegoWIITDB" INTEGER, 
	"idAccesorio" INTEGER, 
	 FOREIGN KEY("idAccesorio") REFERENCES accesorio ("idAccesorio"), 
	 FOREIGN KEY("idJuegoWIITDB") REFERENCES juego_wiitdb ("idJuegoWIITDB")
);
CREATE TABLE rel_accesorio_juego_opcional (
	"idJuegoWIITDB" INTEGER, 
	"idAccesorio" INTEGER, 
	 FOREIGN KEY("idJuegoWIITDB") REFERENCES juego_wiitdb ("idJuegoWIITDB"), 
	 FOREIGN KEY("idAccesorio") REFERENCES accesorio ("idAccesorio")
);
CREATE TABLE rel_juego_genero (
	"idJuegoWIITDB" INTEGER, 
	"idGenero" INTEGER, 
	 FOREIGN KEY("idJuegoWIITDB") REFERENCES juego_wiitdb ("idJuegoWIITDB"), 
	 FOREIGN KEY("idGenero") REFERENCES genero ("idGenero")
);
CREATE TABLE rel_online_features_juego (
	"idJuegoWIITDB" INTEGER, 
	"idFeature" INTEGER, 
	 FOREIGN KEY("idJuegoWIITDB") REFERENCES juego_wiitdb ("idJuegoWIITDB"), 
	 FOREIGN KEY("idFeature") REFERENCES online_features ("idFeature")
);
CREATE TABLE rel_particion_juego (
	"idParticion" INTEGER, 
	"idJuego" INTEGER, 
	 FOREIGN KEY("idParticion") REFERENCES particion ("idParticion"), 
	 FOREIGN KEY("idJuego") REFERENCES juego ("idJuego")
);
CREATE TABLE rel_rating_content_juego (
	"idJuegoWIITDB" INTEGER, 
	"idRatingContent" INTEGER, 
	 FOREIGN KEY("idJuegoWIITDB") REFERENCES juego_wiitdb ("idJuegoWIITDB"), 
	 FOREIGN KEY("idRatingContent") REFERENCES rating_content ("idRatingContent")
);
CREATE TABLE rom (
	"idRom" INTEGER NOT NULL, 
	"idJuegoWIITDB" INTEGER, 
	version VARCHAR(255), 
	name VARCHAR(255), 
	size INTEGER, 
	crc VARCHAR(8), 
	md5 VARCHAR(32), 
	sha1 VARCHAR(40), 
	PRIMARY KEY ("idRom"), 
	 FOREIGN KEY("idJuegoWIITDB") REFERENCES juego_wiitdb ("idJuegoWIITDB")
);
