INSERT INTO juego_descripcion VALUES(2218,998,'EN','North American Hunting Extravaganza','Head out into the wild to track and hunt the most dangerous game in North America. Set your sights on over 20 different wildlife species, with a variety of weapons and hunting equipment at your disposal.');
