INSERT INTO juego_descripcion VALUES(2491,1119,'EN','Smarty Pants','From the EA Casual Entertainment label, Smarty Pants finally levels the playing field for people of all ages, allowing simultaneous play using age-targeted trivia plus action based gestures, making it a truly transgenerational game made exclusively for the Wii™.');
