INSERT INTO juego_descripcion VALUES(2592,1166,'FR','Puppy Luv: votre nouvel ami','Puppy Luv: Votre Nouvel Ami sur Wii est un jeu de gestion de la race canine. Choisissez votre animal de compagnie parmi les 6 races disponibles au départ et éduquez-le avec grand soin si vous voulez remporter des concours. Ces derniers vous permettent de gagner de l''argent que vous pouvez dépenser pour acheter jouets, costumes et autres accessoire pour votre compagnon à 4 pattes.');
INSERT INTO juego_descripcion VALUES(2593,1167,'EN','Puyo Puyo 15th Anniversary','');
