INSERT INTO juego_descripcion VALUES(2775,1255,'EN','Hasbro: Family Game Night','Join your host Mr. Potato Head as you play classic Hasbro games as well as exciting new versions created for the Wii & PS2. Hasbro Family Game Night features family favorites such as CONNECT FOUR, BATTLESHIP, YAHTZEE, BOGGLE, SORRY! and the all new, SORRY! Sliders.');
