INSERT INTO juego_descripcion VALUES(3383,1536,'PT','Piratas das Caraibas: Nos confins do Mundo','Inspirado no grande filme Disney dos Piratas das Caraíbas e no filme campeão de bilheteira do ano passado, "Piratas das Caraíbas: O Cofre do Homem Morto", neste título os jogadores são convidados a mostrar a sua capacidade de lutar contra vilões de má reputação nos convés de grandes navios, explorando cidades e ilhas selvagens e evitando perigos no mar em aventuras nos mundos dos filmes e mais além, vestindo a pele do Capitão Jack Sparrow, de Will Turner e de Elizabeth Swann.');
